import { styled } from "@mui/joy";
import React, { useEffect, useState } from "react";
import { CircularProgressbar } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";
import ScrollTrigger from "react-scroll-trigger";

// style
import "./CircularProgressBar.css";

function CircularProgressBar() {
  const [percentage, setPercentage] = useState(0);
  const [counterOn, setCounterOn] = useState(false);

  useEffect(() => {
    const increasePercentage = () => {
      if (percentage < 70) {
        setPercentage((prevPercentage) => prevPercentage + 1);
      }
    };

    // If counterOn is true, start increasing the percentage
    if (counterOn && percentage < 60) {
      const interval = setInterval(increasePercentage, 40);

      // Clean up interval on unmount or when counterOn becomes false
      return () => clearInterval(interval);
    }
  }, [counterOn, percentage]);

  const handleScrollEnter = () => {
    // Reset the percentage to 0 when the user enters the section
    setPercentage(0);
    setCounterOn(true);
  };

  return (
    <>
      <ScrollTrigger
        onEnter={handleScrollEnter} // Call handleScrollEnter when the section is in view
        onExit={() => setCounterOn(false)} // Stop the counter when the section is out of view
      >
        <div className="progress_parent">
          <div className="container">
            <div className="row">
              <div className="col-sm-12 col-md-12 col-lg-4">
                <div style={{ textAlign: "center" }}>
                  <div className="parent_scroll">
                    <CircularProgressbar
                      value={percentage}
                      text={`${percentage}%`}
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="left_div_scroll">
          <p>Our</p>
          <h3>Expertise</h3>
        </div>
      </ScrollTrigger>
    </>
  );
}

export default CircularProgressBar;
