import React, { useState } from "react";
import "./NumberCounter.css";
import CountUp from "react-countup";
import ScrollTrigger from "react-scroll-trigger";

const NumberCounter = () => {
  const [counterOn, setCounterOn] = useState(false);

  return (
    <div>
      <ScrollTrigger
        onEnter={() => setCounterOn(true)}
        onExit={() => setCounterOn(false)}
      >
        <div className="container">
          <div className="row">
            <div className="col-lg-3 col-md-6 col-sm-12">
              <div className="counter-numbers">
                <h2 className="counter-numbers" data-number="100">
                  {counterOn && (
                    <CountUp start={0} end={100} duration={3} delay={0} />
                  )}
                </h2>
              </div>
            </div>
            <div className="col-lg-3 col-md-6 col-sm-12">
              <div className="counter-numbers">
                <h2 className="counter-numbers" data-number="100">
                  {counterOn && (
                    <CountUp start={0} end={2000} duration={3} delay={0} />
                  )}
                </h2>
              </div>
            </div>
            <div className="col-lg-3 col-md-6 col-sm-12">
              <div className="counter-numbers">
                <h2 className="counter-numbers" data-number="100">
                  {counterOn && (
                    <CountUp start={0} end={7000} duration={3} delay={0} />
                  )}
                </h2>
              </div>
            </div>
            <div className="col-lg-3 col-md-6 col-sm-12">
              <div className="counter-numbers">
                <h2 className="counter-numbers" data-number="100">
                  {counterOn && (
                    <CountUp start={0} end={3000} duration={3} delay={0} />
                  )}
                </h2>
                <p>Best Reviews</p>
              </div>
            </div>
          </div>
        </div>
      </ScrollTrigger>
    </div>
  );
};

export default NumberCounter;
